// Simple MCP ClickUp Integration
const { spawn } = require('child_process');

// Get the action from webhook data
const action = $input.item.json.action || 'list_tasks';
const params = $input.item.json.params || {};

console.log('Executing ClickUp MCP action:', action);

// Execute MCP command
const mcpCommand = spawn('npx', ['-y', '@taazkareem/clickup-mcp-server@latest'], {
  env: {
    ...process.env,
    CLICKUP_API_KEY: process.env.CLICKUP_API_KEY,
    CLICKUP_TEAM_ID: process.env.CLICKUP_TEAM_ID,
    DOCUMENT_SUPPORT: 'true'
  },
  stdio: ['pipe', 'pipe', 'pipe']
});

let result = '';
let error = '';

await new Promise((resolve, reject) => {
  mcpCommand.stdout.on('data', (data) => {
    result += data.toString();
  });

  mcpCommand.stderr.on('data', (data) => {
    error += data.toString();
  });

  mcpCommand.on('close', (code) => {
    if (code === 0) {
      resolve();
    } else {
      reject(new Error(`MCP process exited with code ${code}: ${error}`));
    }
  });

  // Send command to MCP server
  const command = {
    jsonrpc: '2.0',
    id: 1,
    method: 'tools/list'
  };

  mcpCommand.stdin.write(JSON.stringify(command) + '\n');
  mcpCommand.stdin.end();
});

return {
  action: action,
  success: true,
  result: result,
  timestamp: new Date().toISOString()
};
